mapper.select:
SELECT r.item, SUM(r.getnum) getnum, SUM(r.usenum) usenum FROM ( SELECT a.`item`, '' getnum, SUM(a.`use_num`) usenum FROM ly_sfc_assembly a
WHERE a.`operation` = #{operation}
GROUP BY a.`item` UNION SELECT k.`item`, SUM(k.`receive`) getnum,'' FROM ly_workshop_inventory k GROUP BY k.item )r GROUP BY r.item

SELECT so.shoporder_item,li.item_name, li.item_type, st.sfc, sa.item_sfc, sa.create_time, sa.use_num,wr.workcenter, st.workcenter, lo.default_resource, st.byUser FROM ly_sfc_step st LEFT JOIN ly_operation lo ON lo.operation_no = st.operation LEFT JOIN ly_workcenter_relation wr ON wr.workcenter_child = st.workcenter ,ly_sfc_assembly sa , ly_shoporder_sfc ss LEFT JOIN ly_shoporder so ON so.shoporder_no = ss.shoporderID LEFT JOIN ly_item li ON li.item_no = so.shoporder_item WHERE ss.sfc = sa.sfc AND st.site = #{site} AND sa.site = #{site} AND ss.site = #{site} AND lo.site = #{site} AND li.site = #{site} AND so.site = #{site} AND wr.site = #{site} AND st.operation = sa.operation AND st.sfc = sa.sfc
AND sa.item_sfc = #{reverse}
ORDER BY st.createTime, sa.create_time;

SELECT distinct sn.sfc,ss.shoporderid,li.item_type ,ncg.nc_code_group_desc,nc.nc_code_desc,wo.workcenter_no,lss.workcenter,op.default_resource FROM ly_sfc_nc sn , ly_shoporder_sfc ss, ly_shoporder so, ly_item li, ly_sfc_step lss, ly_operation op, ly_nc_code nc, ly_nc_code_relation ncr, ly_nc_code_group ncg, ly_workcenter_relation wr, ly_workcenter wo WHERE sn.sfc = ss.sfc AND ss.shoporderid= so.shoporder_no AND so.shoporder_item = li.item_no AND sn.sfc=lss.sfc AND sn.operation = lss.operation AND sn.operation = op.operation_no AND sn.nc_code = ncr.nc_code AND sn.nc_code = nc.nc_code AND ncr.nc_code_group = ncg.nc_code_group_no AND wr.workcenter_child = lss.workcenter AND wr.workcenter = wo.workcenter_no AND sn.site = #{site}
AND so.shoporder_no = #{scheduler}
AND wo.workcenter_no = #{workcenter} and wo.workcenter_class = 'workshop'
AND wo.workcenter_no = #{workcenter2} and wo.workcenter_class = 'production_line'

SELECT ls.shoporder_no, lss.sfc,lss.status AS sfcStatus,ls.shoporder_item,ls.process_route,ls.status AS shopOrderStatus, ls.workcenter,ls.workline,ls.create_time,ls.site,ls.by_user FROM iemes.ly_shoporder ls LEFT JOIN iemes.ly_shoporder_sfc lss ON ls.shoporder_no = lss.shoporderId WHERE 1=1 and ls.site = #{site}
AND ls.shoporder_no like CONCAT('%', #{shoporder}, '%')
AND lss.sfc like CONCAT('%', #{sfc}, '%')
AND ls.create_time >= #{start_time}
AND ls.create_time <= #{end_time}
AND ls.status like CONCAT('%', #{status}, '%')
AND lss.sfc IN ( SELECT DISTINCT(lsa.sfc) FROM iemes.ly_sfc_assembly lsa LEFT JOIN iemes.ly_workshop_inventory lwi ON lsa.item_sfc = lwi.item_sfc WHERE lsa.batch like CONCAT('%', #{batch}, '%') )

SELECT DISTINCT lsa.item,li.item_name,li.item_desc,li.item_type,lwi.create_time,lwi.by_user,lsa.item_sfc,lwi.batch FROM iemes.ly_sfc_assembly lsa LEFT JOIN iemes.ly_item li ON lsa.item = li.item_no LEFT JOIN iemes.ly_workshop_inventory lwi ON lsa.item_sfc = lwi.item_sfc WHERE 1=1
AND lsa.operation = #{operation}
and lsa.sfc = #{sfc}
and lsa.site = #{site}

SELECT lsn.sfc,lo.operation_description,lor.operationResource_no,lsn.resource,lsn.nc_code,lnc.nc_code_desc,lsn.nc_code_group, lsn.status,lnr.repair_desc,lnr.create_time as handleTime FROM iemes.ly_sfc_nc lsn left join iemes.ly_nc_repair lnr on lsn.repair_id = lnr.id LEFT JOIN iemes.ly_operation_resource lor ON lsn.operation = lor.operation LEFT JOIN iemes.ly_operation lo ON lsn.operation = lo.operation_no LEFT JOIN iemes.ly_nc_code lnc ON lsn.nc_code = lnc.nc_code WHERE 1=1
AND lsn.operation = #{operation}
and lsn.sfc = #{sfc}
and lsn.site = #{site}
